import React, { Component, Profiler } from "react";
import './Tracker.css'
import Grid from '@material-ui/core/Grid';

class Tracker extends Component {
  state = {
    profile: "Lil_Kha1998",
    platform: "psn",
    info: [],
    pics : [],
    stats : [],
  
  };

  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  onSubmit = (e) => {
    e.preventDefault();
    fetch(
      `http://localhost:5000/api/v1/profile/${this.state.platform}/${this.state.profile}`
    )
      .then((res) => res.json())
      .then((res) =>
        this.setState({
          info: res.data.data,
          pics:res.data.platformInfo,
          stats:res.data.data
        })
      )
      .catch((error) => {
        console.log("error");
        console.error(error);
      });
  };

