import React, { Component, Profiler } from "react";


class Tracker extends Component {
  state = {
    profile: "Lil_Kha1998",
    platform: "psn",
    segments: [],
    segmentType:'legend'
  };

  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  onSubmit = (e) => {
    e.preventDefault();
    fetch(
      `http://localhost:5000/api/v1/profile/${this.state.platform}/${this.state.profile}/segments/${this.state.segmentType}`
    )
      .then((res) => res.json())
      .then((res) =>
        this.setState({
          segment: res.data.segments.map((segment) => segment.segmentType),
        })
      )
      .catch((error) => {
        console.log("error");
        console.error(error);
      });
  };

  render() {
    const { profile, platform, stats } = this.state;
    console.log(stats);

    const legend = {
      
    }
   
    return (
      <div className="nav">
        <h1>Stat Tracker</h1>

        <label for="platform"> Choose Platform: </label>
        <form onSubmit={this.onSubmit}>
          <select
            value={platform}
            onChange={this.onChange}
            id="platform"
            name="platform"
          >
            <option value="psn">Playstation</option>

            <option value="xbl">Xbox</option>

            <option value="origin">Origin</option>
          </select>
          <br></br>
          <label for="username">Username: </label>
          <input
            id="username"
            onChange={this.onChange}
            placeholder="Enter Username"
            name="profile"
            value={profile}
          ></input>
          <br></br>
          <button type="submit">Submit</button>
        </form>

        <div>
         
        </div>
      </div>
    );
  }
}

export default Tracker;
