import React, { Component } from "react";
import './Tracker.css'

class Tracker extends Component {
  state = {
    profile: "Lil_Kha1998",
    platform: "psn",
    segmentType: "legend",
    damagePathfinder:{},
    damageWraith: {},
           damageBangalore:{},
            damageMirage:{},
            damageCaustic:{},
            damageGibralter:{},
            damageLoba:{},
            damageLifeline:{},
            damageBloodhound:{},
            damageCrypto:{},
            damageWattson:{},
            damageOctane:{}

  };

  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  onSubmit = (e) => {
    e.preventDefault();
    fetch(
      `http://localhost:5000/api/v1/profile/${this.state.platform}/${this.state.profile}/${this.state.segmentType}`
    )
      .then((res) => res.json())
      .then((res) =>
        this.setState({
             damagePathfinder: res.data.data[0].stats.kills,
            damageWraith: res.data.data[0].stats.kills,
           damageBangalore: res.data.data[0].stats.kills,
            damageMirage: res.data.data[0].stats.kills,
            damageCaustic: res.data.data[0].stats.kills,
            damageGibralter: res.data.data[0].stats.kills,
            damageLoba: res.data.data[0].stats.kills,
            damageLifeline: res.data.data[0].stats.kills,
            damageBloodhound: res.data.data[0].stats.kills,
            damageCrypto: res.data.data[0].stats.kills,
            damageWattson:  res.data.data[0].stats.kills,
            damageOctane: res.data.data[0].stats.kills

         
        })
      )
      .catch((error) => {
        console.log("error");
        console.error(error);
      });
  };

