import React, { Component } from "react";
import e from "express";

class Rank extends Component {
  state = {
    platform: "psn",
    profile: "Lil_Kha1998",
    segmentType: "legend",
    data: {},
  };
  onChange = (e) => {
    e.target.value = e.target;
  };
  onSubmit = () => {
    fetch(
      `http://localhost:5000/api/v1/profile/${this.state.platform}/${this.state.profile}}`
    )
      .then((res) => res.json())
      .then((res) =>
        this.setState({
          data: res.data.data[0].platformInfo,
        })
      );
  };

  render() {
    const {platform,profile} = this.state;
    return (
      <div className="rank-wrapper">
        <div className="rank-content">
        <form onSubmit={this.onSubmit} className="form-content">
            <label for="platform"> Choose Platform: </label>
            <select
              value={platform}
              onChange={this.onChange}
              id="platform"
              name="platform"
            >
              <option value="psn">Playstation</option>

              <option value="xbl">Xbox</option>

              <option value="origin">Origin</option>
            </select>
            <br></br>
            <label for="username">Username: </label>
            <input
              id="username"
              onChange={this.onChange}
              placeholder="Enter Username"
              name="profile"
              value={profile}
            ></input>
            <br></br>
            <button type="submit">Submit</button>
          </form>
        </div>
          <h1>Rank</h1>
          <div className="ranked-output">

          </div>
        </div>
     
    );
  }
}

export default Rank;
