import React, { Component, Profiler } from "react";


class Tracker extends Component {
  state = {
    profile: "Lil_Kha1998",
    platform: "psn",
    stats: [],
  };

  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  onSubmit = (e) => {
    e.preventDefault();
    fetch(
      `http://localhost:5000/api/v1/profile/${this.state.platform}/${this.state.profile}/segments`
    )
      .then((res) => res.json())
      .then((res) =>
        this.setState({
          stats: res.data.segments.map((segment) => segment.stats),
        })
      )
      .catch((error) => {
        console.log("error");
        console.error(error);
      });
  };

  render() {
    const { profile, platform, stats } = this.state;
    console.log(stats);
    const lengend= stats[0].map((d) => d.segments.s)
    return (
      <div className="nav">
        <h1>Stat Tracker</h1>

        <label for="platform"> Choose Platform: </label>
        <form onSubmit={this.onSubmit}>
          <select
            value={platform}
            onChange={this.onChange}
            id="platform"
            name="platform"
          >
            <option value="psn">Playstation</option>

            <option value="xbl">Xbox</option>

            <option value="origin">Origin</option>
          </select>
          <br></br>
          <label for="username">Username: </label>
          <input
            id="username"
            onChange={this.onChange}
            placeholder="Enter Username"
            name="profile"
            value={profile}
          ></input>
          <br></br>
          <button type="submit">Submit</button>
        </form>

        <div>
          {/* {stats[0].map((data) => (
            <h1>{data.stats}</h1>
          ))} */}
        </div>
      </div>
    );
  }
}

export default Tracker;
