import React, { Component } from "react";
import './Tracker.css'

class Tracker extends Component {
  state = {
    profile: "Lil_Kha1998",
    platform: "psn",
    segmentType: "legend",
    stats: []
  };

  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  onSubmit = (e) => {
    e.preventDefault();
    fetch(
      `http://localhost:5000/api/v1/profile/${this.state.platform}/${this.state.profile}/${this.state.segmentType}`
    )
      .then((res) => res.json())
      .then((res) =>
        this.setState({
          stats: res.data.data[0].stats.damage
         
        })
      )
      .catch((error) => {
        console.log("error");
        console.error(error);
      });
  };

  displayDamage = () => {
    const damage = this.state.stats;
    damage.map(item => (
     
          <h1>hello</h1>
    ))
  }

  render() {
    const { profile, platform, stats} = this.state;
    console.log(stats);
    const damage = this.state.stats;
   const displayDamage =  damage.map(item => (   
  <h1>hello</h1>
))
   
    return (
      <div className="tracker-content-main">
        <h1>Stat Tracker</h1>

      <div className="form-wrapper">
        <form onSubmit={this.onSubmit} className='form-content'>

          <label for="platform"> Choose Platform: </label>
          <select
            value={platform}
            onChange={this.onChange}
            id="platform"
            name="platform"
          >
            <option value="psn">Playstation</option>

            <option value="xbl">Xbox</option>

            <option value="origin">Origin</option>
          </select>
          <br></br>
          <label for="username">Username: </label>
          <input
            id="username"
            onChange={this.onChange}
            placeholder="Enter Username"
            name="profile"
            value={profile}
          ></input>
          <br></br>
          <button type="submit">Submit</button>
        </form>

      </div>
        
<div className="stats-output-wrapper">

  <div className="stats-output">
        {displayDamage}
  </div>
 
     </div>
      </div>
    );
  }
}

export default Tracker;
