import React, { Component, Profiler } from "react";
import './Tracker.css'
import Grid from '@material-ui/core/Grid';
const Stat = props => (
  <tr>
  <td>{props.home.username}</td>
  <td>{props.home.description}</td>
  <td>{props.home.pricing}</td>
  <td>{props.home.date.substring(0,10)}</td>

</tr>
)
class Tracker extends Component {
  state = {
    profile: "Lil_Kha1998",
    platform: "psn",
    info: [],
    pics : [],
    stats : [],
  
  };

  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  onSubmit = (e) => {
    e.preventDefault();
    fetch(
      `http://localhost:5000/api/v1/profile/${this.state.platform}/${this.state.profile}`
    )
      .then((res) => res.json())
      .then((res) =>
        this.setState({
          info: res.data.data,
          pics:res.data.platformInfo,
          stats:res.data.data
        })
      )
      .catch((error) => {
        console.log("error");
        console.error(error);
      });
  };

  render() {
    const { profile, platform,pics,info,stats} = this.state;
  
    console.log(info);
    const {homes,users} = this.state;
    const allData = stats.map((postDetail, index) => {
     return <div>
        <h1>{postDetail._id}</h1>
      </div>
    })
    const statData =
stats.map(
   currenthome => {
     return <Home home={currenthome} deletehome={this.deleteHome} key={currenthome._id}/>
   }
 )
   
    return (
      <div className="tracker-content-main">
        <h1>Stat Tracker</h1>

      
        <form onSubmit={this.onSubmit} className='form-content'>

          <label for="platform"> Choose Platform: </label>
          <select
            value={platform}
            onChange={this.onChange}
            id="platform"
            name="platform"
          >
            <option value="psn">Playstation</option>

            <option value="xbl">Xbox</option>

            <option value="origin">Origin</option>
          </select>
          <br></br>
          <label for="username">Username: </label>
          <input
            id="username"
            onChange={this.onChange}
            placeholder="Enter Username"
            name="profile"
            value={profile}
          ></input>
          <br></br>
          <button type="submit">Submit</button>
        </form>
<div className="stats-output-wrapper">
 <div className="stats-output"> 
          <p>Gamertag: {pics.platformUserId}</p>
          <img src= {pics.avatarUrl} className='avatar-pic'></img>
          <p>Stats: {stats}</p>
       {/* <div><ul><li>{playerStats}</li></ul></div> */}
        </div>
        </div>
      </div>
    );
  }
}

export default Tracker;
