import React, { Component, Profiler } from 'react';
import axios from 'axios';

class Tracker extends Component {
    constructor(props) {
        super(props);
        this.state = {platform: 'psn', profile: 'Lil_Kha1998' }
        this.onChange = this.onChange.bind(this)
    };
    onChange(event){
        this.setState({
          platform: event.target.value
        })
       }
    callAPI() {
        fetch(`http://localhost:5000/api/v1/profile/${this.state.platform}/${this.state.profile}`)
            .then(res => res.text())
            .then(res => this.setState({ platform: res, profile: res }));
    }
    
    componentWillMount() {
        this.callAPI();
    }
  
    render() { 
        return ( <div className="nav" >
        <h1>Stat Tracker</h1>
        
        <label for="platform"> Choose Platform: </label>
        <form action="/platform/gamertag" method="GET">
        <select value={this.state.platform} onChange={this.onChange} id="platform">

  <option  value="psn">Playstation</option>

  <option value="xbl" >Xbox</option>

  <option value="origin">Origin</option>
  </select>
  <br></br>
  <label for="username">Username: </label>
        <input id="username" onChange={this.onChange} placeholder="Enter Username"></input ><br>
        </br>
        <button type="submit">Submit</button>
</form>
  
        </div> );
    }
}

export default Tracker;
