import React, { Component, Profiler } from "react";
import './Tracker.css'
var NumberFormat = require('react-number-format');
class Tracker extends Component {
  state = {
    profile: "Lil_Kha1998",
    platform: "psn",
    info: [],
    pics : []
  
  };

  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  onSubmit = (e) => {
    e.preventDefault();
    fetch(
      `http://localhost:5000/api/v1/profile/${this.state.platform}/${this.state.profile}/segments/legend`
    )
      .then((res) => res.json())
      .then((res) =>
        this.setState({
          info: res.data,
          pics:res.data.platformInfo
        })
      )
      .catch((error) => {
        console.log("error");
        console.error(error);
      });
  };

  render() {
    
    const { profile, platform, info, pics } = this.state;
    const infos = Object.keys(info).map((key) => (
      <div className="container">
          <span className="left">{key}</span>
          <span className="right"><NumberFormat value={this.state.info[key].CoinName} displayType={'text'} decimalPrecision={2} thousandSeparator={true} prefix={'$'} /></span>
      </div>
  ));
    console.log(info);
    
   
    return (
      <div className="tracker-content-main">
        <h1>Stat Tracker</h1>

      
        <form onSubmit={this.onSubmit} className='form-content'>

          <label for="platform"> Choose Platform: </label>
          <select
            value={platform}
            onChange={this.onChange}
            id="platform"
            name="platform"
          >
            <option value="psn">Playstation</option>

            <option value="xbl">Xbox</option>

            <option value="origin">Origin</option>
          </select>
          <br></br>
          <label for="username">Username: </label>
          <input
            id="username"
            onChange={this.onChange}
            placeholder="Enter Username"
            name="profile"
            value={profile}
          ></input>
          <br></br>
          <button type="submit">Submit</button>
        </form>
<div className="stats-output-wrapper">
 <div className="stats-output"> 
          <p>Gamertag: {pics.platformUserId}</p>
          <img src= {pics.avatarUrl} className='avatar-pic'></img>
          <p>{info.segments[1].stats.kills.displayValue}</p>
   
        </div>
        </div>
      </div>
    );
  }
}

export default Tracker;
