import React, { Component } from 'react';
import axios from 'axios';

class Tracker extends Component {
    constructor(props) {
        super(props);
        this.state = {platform: 'psn', profile: "" }
        this.onChange
    };
    
    callAPI() {
        fetch(`http://localhost:5000/api/v1/profile/${this.state.platform}/${this.state.profile}`)
            .then(res => res.text())
            .then(res => this.setState({ profile: res }));
    }
    
    componentWillMount() {
        this.callAPI();
    }
  
    render() { 
        return ( <div className="nav" >
        <h1>Tracker</h1>
        
        <label for="platform">Choose Platform: </label>
        <select value={this.state.platform} onChange={this.onChange} id="platform">

  <option  value="psn">Playstation</option>

  <option value="xbl" >Xbox</option>

  <option value="origin">Origin</option>
  </select><br></br>
  <label for="username">Username: </label>
        <input id="username" placeholder="Enter Username"></input><br>
        </br>
        <button>Submit</button>

    <p>{this.state.profile}</p>
        </div> );
    }
}
 
export default Tracker;
