import React, { Component } from "react";
import "./Tracker.css";

class Tracker extends Component {
  state = {
    profile: "Lil_Kha1998",
    platform: "psn",
    segmentType: "legend",
    damagePathfinder: {},
    damageWraith: {},
    damageBangalore: {},
    damageMirage: {},
    damageCaustic: {},
    damageGibralter: {},
    damageLoba: {},
    damageLifeline: {},
    damageBloodhound: {},
    damageCrypto: {},
    damageWattson: {},
    damageOctane: {},
  };

  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  onSubmit = (e) => {
    e.preventDefault();
    fetch(
      `http://localhost:5000/api/v1/profile/${this.state.platform}/${this.state.profile}/${this.state.segmentType}`
    )
      .then((res) => res.json())
      .then((res) =>
        this.setState({
          damagePathfinder: res.data.data[0].stats.kills,
          damageWraith: res.data.data[0].stats.kills,
          damageBangalore: res.data.data[0].stats.kills,
          damageMirage: res.data.data[0].stats.kills,
          damageCaustic: res.data.data[0].stats.kills,
          damageGibralter: res.data.data[0].stats.kills,
          damageLoba: res.data.data[0].stats.kills,
          damageLifeline: res.data.data[0].stats.kills,
          damageBloodhound: res.data.data[0].stats.kills,
          damageCrypto: res.data.data[0].stats.kills,
          damageWattson: res.data.data[0].stats.kills,
          damageOctane: res.data.data[0].stats.kills,
        })
      )
      .catch((error) => {
        console.log("error");
        console.error(error);
      });
  };

  render() {
    //URL params
    const { profile, platform } = this.state;
    //Character kill values
    const {
      damagePathfinder,
      damageWraith,
      damageBangalore,
      damageMirage,
      damageCaustic,
      damageGibralter,
      damageLoba,
      damageLifeline,
      damageBloodhound,
      damageCrypto,
      damageWattson,
      damageOctane,
    } = this.state;
    console.log(damage);

    return (
      <div className="tracker-content-main">
        <h1>Stat Tracker</h1>

        <div className="form-wrapper">
          <form onSubmit={this.onSubmit} className="form-content">
            <label for="platform"> Choose Platform: </label>
            <select
              value={platform}
              onChange={this.onChange}
              id="platform"
              name="platform"
            >
              <option value="psn">Playstation</option>

              <option value="xbl">Xbox</option>

              <option value="origin">Origin</option>
            </select>
            <br></br>
            <label for="username">Username: </label>
            <input
              id="username"
              onChange={this.onChange}
              placeholder="Enter Username"
              name="profile"
              value={profile}
            ></input>
            <br></br>
            <button type="submit">Submit</button>
          </form>
        </div>

        <div className="stats-output-wrapper">
          <div className="stats-output">
            {/* {damage.value ? (<><h1>Damage</h1><p>{damage.value}</p></>) : ( <p>damage loading </p>)} */}
            <h1>Pathfinder</h1>
            <p>{damagePathfinder.value}</p>
            <h1>Wraith</h1>
    <p>{}</p>
            <h1>Bangalore</h1>
    <p>{damageB.value}</p>
            <h1>Mirage</h1>
            <p>{damagePathfinder.value}</p>
            <h1>Caustic</h1>
            <p>{damageCaustic.value}</p>
            <h1>Gibralter</h1>
            <p>{damageGibralter.value}</p>
            <h1>Loba</h1>
            <p>{damageLoba.value}</p>
            <h1>Lifeline</h1>
            <p>{damageLifeline.value}</p>
            <h1>Bloodhound</h1>
            <p>{damageBloodhound.value}</p>
            <h1>Crypto</h1>
            <p>{damageCrypto.value}</p>
            <h1>Wattson</h1>
            <p>{damageWattson.value}</p>
            <h1>Octane</h1>
            <p>{damageOctane.value}</p>
          </div>
        </div>
      </div>
    );
  }
}

export default Tracker;
